--
-- Table structure for table `#__ucm_media`
--

CREATE TABLE `#__ucm_media` (
  `content_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `type_alias` varchar(255) NOT NULL DEFAULT '' COMMENT 'FK to the content types table',
  `title` varchar(255) NOT NULL,
  `alias` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '',
  `body` mediumtext NOT NULL,
  `state` tinyint(1) NOT NULL DEFAULT '0',
  `checked_out_time` varchar(255) NOT NULL DEFAULT '',
  `checked_out` int(10) unsigned NOT NULL DEFAULT '0',
  `access` int(10) unsigned NOT NULL DEFAULT '0',
  `params` text NOT NULL,
  `featured` tinyint(4) unsigned NOT NULL DEFAULT '0',
  `metadata` varchar(2048) NOT NULL COMMENT 'JSON encoded metadata properties.',
  `created_user_id` int(10) unsigned NOT NULL DEFAULT '0',
  `created_by_alias` varchar(255) NOT NULL DEFAULT '',
  `created_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `modified_user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'Most recent user that modified',
  `modified_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `language` char(7) NOT NULL,
  `publish_up` datetime NOT NULL,
  `publish_down` datetime NOT NULL,
  `content_item_id` int(10) unsigned DEFAULT NULL COMMENT 'ID from the individual type table',
  `asset_id` int(10) unsigned DEFAULT NULL COMMENT 'FK to the #__assets table.',
  `images` text NOT NULL,
  `urls` text NOT NULL,
  `hits` int(10) unsigned NOT NULL DEFAULT '0',
  `version` int(10) unsigned NOT NULL DEFAULT '1',
  `ordering` int(11) NOT NULL DEFAULT '0',
  `metakey` text NOT NULL,
  `metadesc` text NOT NULL,
  `catid` int(10) unsigned NOT NULL DEFAULT '0',
  `xreference` varchar(50) NOT NULL COMMENT 'A reference to enable linkages to external data sets.',
  `type_id` int(10) unsigned DEFAULT NULL,
  PRIMARY KEY (`content_id`),
  KEY `tag_idx` (`state`,`access`),
  KEY `idx_access` (`access`),
  KEY `idx_alias` (`alias`),
  KEY `idx_language` (`language`),
  KEY `idx_title` (`title`),
  KEY `idx_modified_time` (`modified_time`),
  KEY `idx_created_time` (`created_time`),
  KEY `idx_content_type` (`type_alias`),
  KEY `idx_core_modified_user_id` (`modified_user_id`),
  KEY `idx_core_checked_out_user_id` (`checked_out`),
  KEY `idx_core_created_user_id` (`created_user_id`),
  KEY `idx_core_type_id` (`type_id`)
)  DEFAULT CHARSET=utf8;

