tinymce.PluginManager.add("joomlaimage", function(a, b) {
	a.addButton("joomlaimage", {
		text: "Joomla",
		icon: "image",
		onclick: function() {
			if (imgLink) {
				a.windowManager.open({
					title  : "Change or upload image",
					url    : imgLink + a.getContainer().id,
					width  : 900,
					height : 600,
					buttons: [{
						text   : "Insert",
						onclick: function () {
							var b = a.windowManager.getWindows()[0];
							a.insertContent("<img src=\"" + b.getContentWindow().document.getElementById("f_url").value + "\" alt=\"\" />"), b.close()
						}
					}, {
						text   : "Close",
						onclick: "close"
					}]
				})
			}
		}
	}), a.addMenuItem("joomlaimage", {
		text: "Joomla Insert image",
		context: "insert",
		icon: "image",
		onclick: function() {
			if (imgLink) {
				a.windowManager.open({
					title  : "Change or upload image",
					url    : imgLink + a.getContainer().id,
					width  : 900,
					height : 600,
					buttons: [{
						text   : "Insert",
						onclick: function () {
							var b = a.windowManager.getWindows()[0];
							a.insertContent("<img src=\"" + b.getContentWindow().document.getElementById("f_url").value + "\" alt=\"\" />"), b.close()
						}
					}, {
						text   : "Close",
						onclick: "close"
					}]
				})
			}
		}
	})
});