tinymce.PluginManager.add("joomlapager", function(a, b) {
	a.addButton("joomlapager", {
		text: "J Page Break",
		title: "Joomla Page Break",
		icon: "copy",
		onclick: function() {
				a.windowManager.open({
					title  : "Joomla Page Break",
					url    : b + "/index.html",
					width  : 300,
					height : 200,
					buttons: [{
						text   : "Insert",
						onclick: function () {
							var b = a.windowManager.getWindows()[0];
							a.insertContent('<hr class="system-pagebreak" title="'+
							b.getContentWindow().document.getElementById("title").value+'" alt="'+
							b.getContentWindow().document.getElementById("alt").value+'" />'),
								b.close()
						}
					},
					{
					text   : "Close",
					onclick: "close"
				}]
				})
			}
	}),
	a.addMenuItem("joomlapager", {
		text: "Joomla Page Break",
		context: "insert",
		icon: "copy",
		onclick: function() {
				a.windowManager.open({
					title  : "Joomla Page Break",
					url    : "index.php?option=com_content&amp;view=article&amp;layout=pagebreak&amp;tmpl=component&amp;e_name=" + a.getContainer().id,
					width  : 900,
					height : 600,
					buttons: [{
						text   : "Insert",
						onclick: function () {
							var b = a.windowManager.getWindows()[0];
							a.insertContent('<hr class="system-pagebreak" title="'+
							b.getContentWindow().document.getElementById("title").value+'" alt="'+
							b.getContentWindow().document.getElementById("alt").value+'" />'),
								b.close()
						}
					},
					{
						text   : "Close",
						onclick: "close"
					}]
				})
		}
	})
});