tinymce.PluginManager.add("joomlapager", function(a, b) {
	a.addButton("joomlapager", {
		text: "J Page Break",
		title: "Joomla Page Break",
		icon: "copy",
		onclick: function () {
			a.windowManager.open({
				title   : 'Page Break',
				body    : [{
					type : 'textbox',
					name : 'title',
					label: 'Title:'
				},
					{
						type : 'textbox',
						name : 'id',
						label: 'Alternative:'
					}],
				onsubmit: function (e) {
					a.insertContent('<hr class="system-pagebreak" title="' + e.data.title + '" alt="' + e.data.id + '" />');
				}
			});
		}
	}),
	a.addMenuItem("joomlapager", {
		text: "J Page Break",
		title: "Joomla Page Break",
		icon: "copy",
		onclick: function () {
			a.windowManager.open({
				title   : 'Page Break',
				body    : [{
					type : 'textbox',
					name : 'title',
					label: 'Title:'
				},
					{
						type : 'textbox',
						name : 'id',
						label: 'Alternative:'
					}],
				onsubmit: function (e) {
					a.insertContent('<hr class="system-pagebreak" title="' + e.data.title + '" alt="' + e.data.id + '" />');
				}
			});
		}
	})
});