customElements.define("joomla-hidden-mail",class extends HTMLElement{connectedCallback(){let t;if("1"===this.getAttribute("is-link")){(t=document.createElement("a")).setAttribute("href","mailto:"+window.atob(this.getAttribute("first"))+"@"+window.atob(this.getAttribute("last")));for(let e=0,i=this.attributes.length;e<i;++e){const i=this.attributes.item(e).nodeName;if(i){if(["is-link","is-email","first","last","text"].indexOf(i)>-1)continue;const s=this.attributes.item(e).nodeValue;t.setAttribute(i,s)}}}else t=document.createElement("span");this.getAttribute("text")?t.innerHTML=window.atob(this.getAttribute("text")):t.innerText=window.atob(this.getAttribute("first"))+"@"+window.atob(this.getAttribute("last")),this.innerText="",this.appendChild(t)}});